import { Link } from "react-router-dom";
import {useNavigate} from "react-router-dom"
import Feed from './Feed';
import Signup from './Signup'
import React, {useContext} from 'react'
import {useState} from "react"
import axiosInstance from "../axios";
import "./Login.css" 
import "./SKYGRAM.png"

function Login() {

    const history = useNavigate();
	// const initialFormData = Object.freeze({
	// 	email: '',
	// 	password: '',

	// });

	const [formData, setFormData] = useState({});

	const handleChange = (e) => {
		setFormData({
			...formData,
			[e.target.id]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
        console.log(formData)
        const headers = {
            'Content-Type': 'application/json',
            'Accept':'application/json'
            }
            // fetch('/token/',{
            //     headers: {
            //       'Accept': 'application/json',
            //       'Content-Type': 'application/json'
            //     },
            //     method: "POST",
            //     body: JSON.stringify({username: formData.username, password: formData.password})
            // }).then(r => r.json())
            // .then(d => console.log(d))
		axiosInstance
			.post(`/token/`, {
				username: formData.username,
				password: formData.password,
			},{
                withCredentials: true,
                headers: headers
              })
			.then((res) => {
				localStorage.setItem('access_token', res.data.access);
				localStorage.setItem('refresh_token', res.data.refresh);
				axiosInstance.defaults.headers['Authorization'] =
					'JWT ' + localStorage.getItem('access_token');
				history.push('');
			});
	};
    return (
        <div class="container">
        <div class="box">
            <div class="heading"></div>
            <form class="login-form" >
                <div class="field">
                    <input
                    onChange={handleChange}
                    id="username"
                    type="name"
                    placeholder="Username"
                    />
                <label for="username">Username</label>
                </div>
                <div class="field">
                <input onChange={handleChange} id="password" type="password" placeholder="Password" />
                <label for="password">Password</label>
                </div>
                <button onClick={handleSubmit}class="login-button" title="login">Log In</button>
                <div class="separator">
                <div class="line"></div>
                <p>OR</p>
                <div class="line"></div>
                </div>
                <div class="other">
                <button class="fb-login-btn" type="button">
                <i class="fa fa-facebook-official fb-icon"></i>
                <span class="">Log in with Facebook</span>
                </button>
                <a class="forgot-password" href="#">Forgot password?</a>
                </div>
            </form>
        </div>
        <div class="box">
            <p>Don't have an account?</p> <Link to="/" element={<Signup/>}>Sign Up</Link>
        </div>
        <div className="footer">
            <p>Â© 2023 SKYGRAM</p>
        </div>

        </div>

    )
}

export default Login
