import React, {useEffect} from 'react'
import {Link, useNavigate} from 'react-router-dom'
import axiosInstance from '../axios'
import "./NavBar.css"
import SKYGRAMLogo from './SKYGRAM.png'
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import ExploreIcon from '@mui/icons-material/Explore';
import BookmarkBorderIcon from "@mui/icons-material/BookmarkBorder";
import ChatIcon from '@mui/icons-material/Chat';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import LogoutIcon from '@mui/icons-material/Logout';


function NavBar(){
    const history = useNavigate()
    const handleLogout = () => {
        axiosInstance.post('/logout', {
          refresh_token: localStorage.getItem('refresh_token'),
        })
        .then(() => {
          localStorage.removeItem('access_token');
          localStorage.removeItem('refresh_token');
          delete axiosInstance.defaults.headers.common['Authorization'];
          history('/login');
        })
        .catch((error) => {
          console.error('Logout failed:', error);
        });
      }

      useEffect(() => {
        handleLogout();
      }, []);


    return (
        <div className='NavBar'>

        <img className='NavBar__logo' src={SKYGRAMLogo} alt=''/>

        <div className="NavBar__buttons"> 
        <Link to="/">
        <button className="NavBar__button">
            <HomeIcon />
            <span>Home</span>
        </button>
        </Link>
        <button className="NavBar__button">
            <SearchIcon />
            <span>Search</span>
        </button>
        <button className="NavBar__button">
            <ExploreIcon />
            <span>Explore</span>
        </button>
        <button className="NavBar__button">
            <ChatIcon />
            <span>Messages</span>
        </button>
        <button className="NavBar__button">
            <BookmarkBorderIcon />
            <span>Saved</span>
        </button>
        <button className="NavBar__button">
            <FavoriteBorderIcon />
            <span>Notifications</span>
        </button>
        <Link to='/create'>
        <button className="NavBar__button">
            <AddCircleOutlineIcon />
            <span>Create</span>
        </button>    
        </Link>
        {/* <button className="NavBar__button">
            <Avatar>
            {user.username ? user.username.charAt(0).toUpperCase() : "A"}
            </Avatar>
            <span>
            {user.username}{" "}
            <button onClick={handelLogout} className="logout__button">
                Logout
            </button>
            </span>
        </button> */}
        </div>
        <div className="NavBar__more">
        <button onClick={handleLogout} className="NavBar__button">
            <LogoutIcon />
            <span className="NavBar__buttonText">Logout</span>
        </button>
        </div>
    </div>

    );
}

export default NavBar
