import { Link } from "react-router-dom";
import Feed from './Feed';
import Signup from './Signup'
import React from 'react'
// import AuthContext from '../context/AuthContext'
import "./Login.css" 
import "./SKYGRAM.png"

function Login() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useState('');

    const handleLogin = () => {
        fetch('http://your-api-endpoint/login/', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username, password }),
        })
            .then((response) => response.json())
            .then((data) => {
            setToken(data.token);
            })
            .catch((error) => {
            });
        };
    return (
        <div class="container">
        <div class="box">
            <div class="heading"></div>
            <form class="login-form" >
                <div class="field">
                    <input
                    id="username"
                    type="name"
                    laceholder="Phone number, username, or email"
                    onChange={(e) => setUsername(e.target.value)}/>
                <label for="username">Username</label>
                </div>
                <div class="field">
                <input onChange={(e) => setPassword(e.target.value)} id="password" type="password" placeholder="password" />
                <label for="password">Password</label>
                </div>
                <button class="login-button" title="login" onClick={handleLogin}>Log In</button>
                <div class="separator">
                <div class="line"></div>
                <p>OR</p>
                <div class="line"></div>
                </div>
                <div class="other">
                <button class="fb-login-btn" type="button">
                <i class="fa fa-facebook-official fb-icon"></i>
                <span class="">Log in with Facebook</span>
                </button>
                <a class="forgot-password" href="#">Forgot password?</a>
                </div>
            </form>
        </div>
        <div class="box">
            <p>Don't have an account? <a class="signup" href="#">Sign Up</a></p>
        </div>
        <div className="footer">
            <p>© 2023 SKYGRAM</p>
        </div>

        </div>

    )
}

export default Login
